mapper.insert:


  SELECT BOARD_SEQ.NEXTVAL
    FROM DUAL

INSERT INTO BOARD_T (
    BOARD_NO
  , BOARD_TITLE
  , BOARD_CONTENTS
  , USER_NO
  , CREATE_DT
  , MODIFY_DT
) VALUES (
    #{boardNo}
  , #{boardTitle}
  , #{boardContents}
  , #{user.userNo}
  , CURRENT_TIMESTAMP
  , CURRENT_TIMESTAMP
)

mapper.update:
UPDATE BOARD_T
	SET BOARD_HIT = BOARD_HIT + 1
WHERE BOARD_NO = #{boardNo}
